parameters.jsCode:
// Get workflow static data to track last processed version
const staticData = $getWorkflowStaticData('global');

// Get the latest tag from the GitHub API response
// n8n splits the array response into separate items, so first item = latest tag
const items = $input.all();
if (!items || items.length === 0 || !items[0].json) {
  return [{ json: { isNew: false, version: null, error: 'No tags found' } }];
}

// First item is the latest tag (GitHub returns tags sorted by creation date desc)
const latestTag = items[0].json;
if (!latestTag.name) {
  return [{ json: { isNew: false, version: null, error: 'Invalid tag data' } }];
}

// Extract version (strip 'v' prefix if present)
let version = latestTag.name;
if (version.startsWith('v')) {
  version = version.substring(1);
}

// Check if this is a new version
const lastVersion = staticData.lastVersion_firemerge || null;
const isNew = lastVersion !== version;

return [{
  json: {
    isNew: isNew,
    version: version,
    lastVersion: lastVersion,
    tagName: latestTag.name,
    commitSha: latestTag.commit?.sha || null
  }
}];

