// Update static data with the new version
const staticData = $getWorkflowStaticData('global');
const version = $('Check New Version').item.json.version;
staticData.lastVersion_firemerge = version;

return [{
  json: {
    success: true,
    version: version,
    message: `Updated last version to ${version}`
  }
}];
